import { Dimensions, StyleSheet } from "react-native";


const  {width, height} = Dimensions.get("screen");

const styles = StyleSheet.create({
    header: {
        height: 50, width: '100%',
        backgroundColor: '#007dc6',
        justifyContent: 'center',
        flexDirection: 'row',
        marginTop: 20,
        marginBottom: 20,
    },

    header_view:{
        flex: 12,
        justifyContent: 'center',
        alignItems:'center',
    },

    header_image:{
        resizeMode: 'stretch', 
        height: 50, width: '80%', 
        borderRadius:10
    },

    container: {
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center',
    },

    inputContainer:{
        marginVertical:30,
    },

    input:{
        borderColor:"black",
        borderWidth:1,
        width:width / 1.4,
        height:30,

    }
})

export {styles};
