import { StatusBar } from 'expo-status-bar';
import React, { Component } from 'react';
import { Text, View, ScrollView, Image, TouchableOpacity } from 'react-native';
import Input from './components/Input';
import { styles } from './styles/styles';

export default class App extends Component {
  state = {
    m1: "",
    m2: "", d1: "", d2: "", D1: "", D2: "",

    GenelFormul: "",
    GenelDisliHesabiFormulu: "",
    SomunGramajDegisimiIcinDisliHesabi: "",
    CapDegisimiIcinDisliHesabi: "",

  };
  //   state.m1 veya state["m1"]
  onChangeHandler = (event, name) => {
    const names = name;
    const values = event;
    this.setState({ [names]: parseFloat(values) }) // []-> bunla array bir value tanımlamış oluyın aslında
    // [names] => bu doğru
    //daha yeni çalışıyordu bu şekilde,olabilir ama doğru değil array oluyor


  };
  // bi dkk
  onSubmitHandler = () => {
    // bu şekilde yaparsan aşağıdakilere gerke kalmaz okey , mantıklı
    const mBir = parseFloat(this.state.m1);
    const mIki = parseFloat(this.state.m2);
    const dBir = parseFloat(this.state.d1);
    const dIki = parseFloat(this.state.d2);
    const DBir = parseFloat(this.state.D1);
    const DIki = parseFloat(this.state.D2);

    alert(mBir)

  }

  render() {
    return (
      <View style={styles.view}>

        {/* header start */}
        <View style={styles.header}>
          <View style={styles.header_view}>
            {/* <Text style={{color: 'white', fontSize: 25 }}>Öz civata</Text> */}
            <Image style={styles.header_image} source={require('./assets/ozcivata.jpeg')} />
          </View>
        </View>
        {/* header finish */}


        <ScrollView>
          <View style={styles.container}>
            <Text>Dişli Hesabı</Text>

            { // buda farklı bir çözüm, bu güzel çözümmüş sevdim
              [
                { label: "m1: Vurulan somun gramajı (gr)", value: "m1" },
                { label: "m1: Vurulan somun gramajı (gr)", value: "m2" },
                { label: "m1: Vurulan somun gramajı (gr)", value: "d1" },
                { label: "m1: Vurulan somun gramajı (gr)", value: "d2" },
                { label: "m1: Vurulan somun gramajı (gr)", value: "D1" },
                { label: "m1: Vurulan somun gramajı (gr)", value: "D2" },
              ].map((item, index) => <Input
                key={index}
                label={item.label}
                onChangeText={event => this.onChangeHandler(event, item.value)}
                value={this.state[item.value]} // niye nokta gelmiyor mu? gelmyor this.state bir obje 
              />)}
            <Input
              label="m1: Vurulan somun gramajı (gr)"
              onChangeText={event => this.onChangeHandler(event, "m1")}
              value={this.state.m1}
            />
            {/* bi dakka kanka
             */}
            <Input
              label="m2: Vurula somun gramajı (gr)"
              onChangeText={event => this.onChangeHandler(event, "m1")}
              value={this.state.m1}
            />



            <View style={styles.inputContainer}>
              <TouchableOpacity
                style={styles.button}
                onPress={this.onSubmitHandler}
              >
                <Text style={{ color: "#fff" }}>Hesapla</Text>
              </TouchableOpacity>
            </View>

            <View style={styles.inputContainer}>
              <Text style={styles.result}>GenelFormul : {this.state.GenelFormul} </Text>
            </View>
            <View style={styles.inputContainer}>
              <Text style={styles.result}>GenelFormul : {this.state.GenelFormul} </Text>
            </View>
            <View style={styles.inputContainer}>
              <Text style={styles.result}>GenelFormul : {this.state.GenelFormul} </Text>
            </View>
            <View style={styles.inputContainer}>
              <Text style={styles.result}>GenelFormul : {this.state.GenelFormul} </Text>
            </View>
            <View style={styles.inputContainer}>
              <Text style={styles.result}>GenelFormul : {this.state.GenelFormul} </Text>
            </View>

          </View>

        </ScrollView>
        <StatusBar style="auto" />
      </View>
    );
  }
}
